section.script:
        //
        (function () {
          const track = document.getElementById('exploreTrack');
          const prevBtn = document.getElementById('explorePrev');
          const nextBtn = document.getElementById('exploreNext');
          const dotsContainer = document.getElementById('exploreDots');
          const slides = document.querySelectorAll('.explore-slide');
          let currentIndex = 0;
          const totalSlides = slides.length;

          // Create Dots
          slides.forEach((_, i) => {
            const dot = document.createElement('div');
            dot.classList.add('explore-dot');
            if (i === 0) dot.classList.add('active');
            dot.addEventListener('click', () => goToSlide(i));
            dotsContainer.appendChild(dot);
          });

          const dots = document.querySelectorAll('.explore-dot');

          function updateSlider() {
            track.style.transform = `translateX(-${currentIndex * 100}%)`;
            dots.forEach((d, i) => d.classList.toggle('active', i === currentIndex));
          }

          function goToSlide(index) {
            if (index < 0) index = totalSlides - 1;
            if (index >= totalSlides) index = 0;
            currentIndex = index;
            updateSlider();
          }

          prevBtn.addEventListener('click', () => goToSlide(currentIndex - 1));
          nextBtn.addEventListener('click', () => goToSlide(currentIndex + 1));

          // Swipe Functionality
          let touchStartX = 0;
          let touchEndX = 0;

          track.addEventListener('touchstart', e => {
            touchStartX = e.changedTouches[0].screenX;
          }, { passive: true });

          track.addEventListener('touchend', e => {
            touchEndX = e.changedTouches[0].screenX;
            handleSwipe();
          }, { passive: true });

          function handleSwipe() {
            const threshold = 50;
            if (touchStartX - touchEndX > threshold) {
              goToSlide(currentIndex + 1); // Swipe Left -> Next
            }
            if (touchEndX - touchStartX > threshold) {
              goToSlide(currentIndex - 1); // Swipe Right -> Prev
            }
          }
        })();
        //

div.script:
            //
            function sendToWhatsApp(event) {
              event.preventDefault();

              // Get form values
              var name = document.getElementById('userName').value;
              var email = document.getElementById('userEmail').value;
              var message = document.getElementById('userMessage').value;

              // Create WhatsApp message
              var whatsappMessage = 'Hello! I am ' + name + '.%0A';
              whatsappMessage += 'Email: ' + email + '%0A';
              whatsappMessage += 'Message: ' + message;

              // WhatsApp number (without + and spaces)
              var whatsappNumber = '94778626300';

              // Create WhatsApp URL
              var whatsappURL = 'https://wa.me/' + whatsappNumber + '?text=' + whatsappMessage;

              // Open WhatsApp in new tab
              window.open(whatsappURL, '_blank');

              // Reset form
              document.getElementById('contactForm').reset();

              return false;
            }
