class PremiumSlider { constructor() { this.currentSlide = 0; this.slides = document.querySelectorAll('.slide'); this.totalSlides = this.slides.length; this.sliderWrapper = document.getElementById('sliderWrapper'); this.dotsContainer = document.getElementById('dotsContainer'); this.progressBar = document.getElementById('progressBar'); this.autoPlayInterval = null; this.autoPlayDuration = 5000; this.progressInterval = null; this.init() } init() { this.createDots(); this.attachEventListeners(); this.startAutoPlay(); this.updateSlider() } createDots() { for (let i = 0; i < this.totalSlides; i++) { const dot = document.createElement('div'); dot.classList.add('dot'); if (i === 0) dot.classList.add('active'); dot.addEventListener('click', () => this.goToSlide(i)); this.dotsContainer.appendChild(dot) } } attachEventListeners() { document.getElementById('prevBtn').addEventListener('click', () => { this.prevSlide(); this.resetAutoPlay() }); document.getElementById('nextBtn').addEventListener('click', () => { this.nextSlide(); this.resetAutoPlay() }); this.sliderWrapper.addEventListener('mouseenter', () => this.pauseAutoPlay()); this.sliderWrapper.addEventListener('mouseleave', () => this.startAutoPlay()); document.addEventListener('keydown', (e) => { if (e.key === 'ArrowLeft') { this.prevSlide(); this.resetAutoPlay() } else if (e.key === 'ArrowRight') { this.nextSlide(); this.resetAutoPlay() } }); let touchStartX = 0; let touchEndX = 0; this.sliderWrapper.addEventListener('touchstart', (e) => { touchStartX = e.changedTouches[0].screenX }); this.sliderWrapper.addEventListener('touchend', (e) => { touchEndX = e.changedTouches[0].screenX; if (touchStartX - touchEndX > 50) { this.nextSlide(); this.resetAutoPlay() } else if (touchEndX - touchStartX > 50) { this.prevSlide(); this.resetAutoPlay() } }) } updateSlider() { this.sliderWrapper.style.transform = `translateX(-${this.currentSlide * 100}%)`; this.slides.forEach((slide, index) => { slide.classList.toggle('active', index === this.currentSlide) }); const dots = this.dotsContainer.querySelectorAll('.dot'); dots.forEach((dot, index) => { dot.classList.toggle('active', index === this.currentSlide) }) } nextSlide() { this.currentSlide = (this.currentSlide + 1) % this.totalSlides; this.updateSlider() } prevSlide() { this.currentSlide = (this.currentSlide - 1 + this.totalSlides) % this.totalSlides; this.updateSlider() } goToSlide(index) { this.currentSlide = index; this.updateSlider(); this.resetAutoPlay() } startAutoPlay() { this.autoPlayInterval = setInterval(() => { this.nextSlide() }, this.autoPlayDuration); this.startProgressBar() } pauseAutoPlay() { clearInterval(this.autoPlayInterval); clearInterval(this.progressInterval); this.progressBar.style.width = '0%' } resetAutoPlay() { this.pauseAutoPlay(); this.startAutoPlay() } startProgressBar() { let progress = 0; const increment = 100 / (this.autoPlayDuration / 50); this.progressInterval = setInterval(() => { progress += increment; if (progress >= 100) { progress = 0 } this.progressBar.style.width = `${progress}%` }, 50) } } document.addEventListener('DOMContentLoaded', () => { new PremiumSlider() });
