    //
    // ROOMS TOGGLE LOGIC
    function showRooms() {
      // Toggle visibility
      const homeView = document.getElementById('home-view');
      const mainContent = document.getElementById('main-content');
      const roomsSection = document.getElementById('rooms-section');
      const mealsSection = document.getElementById('meals-section');
      const gallerySection = document.getElementById('gallery-section');

      if (homeView) homeView.style.display = 'none';
      if (mainContent) mainContent.style.display = 'none';
      if (mealsSection) mealsSection.style.display = 'none';
      if (gallerySection) gallerySection.style.display = 'none';
      if (roomsSection) roomsSection.style.display = 'block';

      // Scroll to top
      window.scrollTo({
        top: 0,
        behavior: 'smooth'
      });
    }

    function showHome(scrollToTop = true) {
      // Toggle visibility
      const homeView = document.getElementById('home-view');
      const mainContent = document.getElementById('main-content');
      const roomsSection = document.getElementById('rooms-section');
      const mealsSection = document.getElementById('meals-section');
      const gallerySection = document.getElementById('gallery-section');

      if (roomsSection) roomsSection.style.display = 'none';
      if (mealsSection) mealsSection.style.display = 'none';
      if (gallerySection) gallerySection.style.display = 'none';
      if (homeView) homeView.style.display = 'block';
      if (mainContent) mainContent.style.display = 'block';

      // Scroll to top only if requested
      if (scrollToTop) {
        window.scrollTo({
          top: 0,
          behavior: 'smooth'
        });
      }
    }

    function showGallery() {
      const homeView = document.getElementById('home-view');
      const mainContent = document.getElementById('main-content');
      const roomsSection = document.getElementById('rooms-section');
      const mealsSection = document.getElementById('meals-section');
      const gallerySection = document.getElementById('gallery-section');

      if (homeView) homeView.style.display = 'none';
      if (mainContent) mainContent.style.display = 'none';
      if (roomsSection) roomsSection.style.display = 'none';
      if (mealsSection) mealsSection.style.display = 'none';
      if (gallerySection) gallerySection.style.display = 'block';



      window.scrollTo({
        top: 0,
        behavior: 'smooth'
      });
    }

    function showMeals() {
      // Hide all other sections
      const homeView = document.getElementById('home-view');
      const mainContent = document.getElementById('main-content');
      const roomsSection = document.getElementById('rooms-section');
      const mealsSection = document.getElementById('meals-section');
      const gallerySection = document.getElementById('gallery-section');

      if (homeView) homeView.style.display = 'none';
      if (mainContent) mainContent.style.display = 'none';
      if (roomsSection) roomsSection.style.display = 'none';
      if (mealsSection) mealsSection.style.display = 'block';
      if (gallerySection) gallerySection.style.display = 'none';

      // Close mobile menu if open


      // Scroll to top
      window.scrollTo({
        top: 0,
        behavior: 'smooth'
      });
    }
